import React, { Component } from 'react';
import DatabaseEntry from './DatabaseEntry';

class AlbumDatabase extends Component {
    static entryId = 0;
    
    constructor(props) {
        super(props);
        
        this.state = {
            entries: [],
            curId: AlbumDatabase.entryId,
            curArtist: "",
            curTitle: "",
            curGenre: "",
            curDate: ""
        };

        // this bindings
        this.addEntry = this.addEntry.bind(this);
    }
    
    addEntry(event) {
        // add <DatabaseEntry idVal={AlbumDatabase.entryId} artist={curArtist} title={curTitle} genre={curGenre} date={curDate} />
        let curEntries = this.state.entries;

        curEntries.push([
            AlbumDatabase.entryId, 
            this.state.curArtist, 
            this.state.curTitle,
            this.state.curGenre,
            this.state.curDate
        ]);
        
        this.setState((state, props) => { 
            return { 
                entries: curEntries
            }
        });
        
        AlbumDatabase.entryId += 1;

        event.preventDefault();
    }

    render() {
        let entryTags = this.state.entries.map(
            x => <DatabaseEntry key={x[0]} idVal={x[0]} artist={x[1]} title={x[2]} genre={x[3]} date={x[4]}/>
        );

        return (
            <div>
                <div>{entryTags}</div>
                <form onSubmit={this.addEntry}>
                    <button type='submit'>ADD</button>
                </form>
            </div>
        );
    }
}

export default AlbumDatabase;
