import React, { Component } from 'react';
import DatabaseEntry from './DatabaseEntry';

class AlbumDatabase extends Component {
    static entryId = 0;
    
    constructor(props) {
        super(props);
        
        this.state = {
            entries: []
        };

        // this bindings
        this.addEntry = this.addEntry.bind(this);
    }
    
    addEntry(curArtist, curTitle, curGenre, curDate) {
        // add <DatabaseEntry idVal={AlbumDatabase.entryId} artist={curArtist} title={curTitle} genre={curGenre} date={curDate} />
        this.setState((state, props) => { 
            let curEntries = state.entries;

            curEntries.push([AlbumDatabase.entryId, curArtist, curTitle, curGenre, curDate]);

            return { 
                entries: curEntries
            }
        });
        
        AlbumDatabase.entryId += 1;
    }

    render() {
        let entryTags = this.state.entries.map(
            x => <DatabaseEntry idVal={x[0]} artist={x[1]} title={x[2]} genre={x[3]} date={x[4]}/>
        );

        return (
            <div>
                <div>{entryTags}</div>
                <form onSubmit={this.addEntry}>
                    <button type='submit'>ADD</button>
                </form>
            </div>
        );
    }
}

export default AlbumDatabase;
